tuv.seg:
=begin code
my $p1 = Promise.new;
say $p1.status;         # Planned;
$p1.keep('result');
say $p1.status;         # Kept
say $p1.result;         # result

=begin code
my $p1 = Promise.new;
say $p1.status;         # Planificado;
$p1.keep('resultado');
say $p1.status;         # Guardado
say $p1.result;         # resultado

my $p2 = Promise.new;
$p2.break('oh no');
say $p2.status;         # Broken
say $p2.result;         # dies with "oh no"
=end code

my $p2 = Promise.new;
$p2.break('oh no');
say $p2.status;         # Rotura
say $p2.result;         # muere con "oh no"
=end code
